genome = ""

def PatternMatching(genome, pattern):
    k = len(pattern)
    positions = []
    for p in range(len(genome)-k+1):
        if pattern == genome[p:p+k]:
            positions.append(p)
    return positions 

count1 = len(PatternMatching(genome,"ATGATCAAG"))
count2 = len(PatternMatching(genome,"CTTGATCAT"))
print(count1+count2)

